mapper.delete:
delete
                            from CART
                            where CART_INDEX = #{cartIndex}
                            and USER_INDEX = #{userIndex}

mapper.update:
update cart
set PRODUCT_COUNT=#{productCount}
where CART_INDEX = #{cartIndex}

mapper.select:
select * from CART
where USER_INDEX = #{userIndex}

    CART_INDEX = #{cartIndex}

select *
                                                       from VIEW_CART
                                                       where USER_INDEX = #{userIndex}
                                                         and PRODUCT_NAME = #{productName}
